function addBoundingBox(scene, object) {
  const boxHelper = new THREE.BoxHelper(object, 0xffff00);
  scene.add(boxHelper);
}

// Usage: addBoundingBox(scene, myObject);

function visualizeNormals(scene, object) {
  const normalsHelper = new THREE.VertexNormalsHelper(object, 2, 0x00ff00);
  scene.add(normalsHelper);
}

// Usage: visualizeNormals(scene, myMesh);

function setGlobalWireframe(scene, enabled = true) {
  scene.traverse((child) => {
    if (child.isMesh && child.material) {
      if (enabled) {
        if (!child.userData.originalMaterial) {
          child.userData.originalMaterial = child.material;
        }
        child.material = new THREE.MeshBasicMaterial({
          color: 0xffffff,
          wireframe: true
        });
      } else if (child.userData.originalMaterial) {
        child.material = child.userData.originalMaterial;
        delete child.userData.originalMaterial;
      }
    }
  });
}

// Usage: setGlobalWireframe(scene, true);

